mapper.insert:
INSERT INTO item (
    title, description, type, location, item_time, image_url, status, user_id, create_time, update_time
) VALUES (
    #{title}, #{description}, #{type}, #{location}, #{itemTime}, #{imageUrl}, #{status}, #{userId}, #{createTime}, #{updateTime}
)

mapper.update:
UPDATE item
title = #{title}, description = #{description}, type = #{type}, location = #{location}, item_time = #{itemTime}, image_url = #{imageUrl}, status = #{status}, update_time = #{updateTime}
WHERE id = #{id}

UPDATE item
SET status = #{status}, update_time = NOW()
WHERE id = #{id}

mapper.select:
SELECT

FROM item


        AND status = #{status}


        AND type = #{type}


ORDER BY create_time DESC

SELECT COUNT(*)
FROM item
WHERE user_id = #{userId}
